import React, { Component } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

export default class CreateExercise extends Component {
    constructor(props) {
        super(props);

        this.onChangeUsername = this.onChangeUsername.bind(this); 
        this.onSubmit = this.onSubmit.bind(this);

        this.setState = {
            username: ''
        }
    }

    componentDidMount() {
        this.setState({
            users: ['test user'],
            username: 'test user'
        })
    }

    onChangeUsername(e) { //quand l'utilisateur remplit le username dans le formulaire, ça va appeler cette fonction
        this.setState({
            username: e.target.value //le target c'est la textbox et la value est la valeur que l'utilisateur entre
        })
    }

    onSubmit(e){
        e.preventDefault(); //empêche le formulaire d'être envoyé par défaut (si les cases sont vides)

        const user = {
            username: this.state.username
        }

        console.log(user);

        window.location = '/';
    }

    render() {
        return (
            <div className="container">
                <h3>Create new exercise </h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>

                        <select ref="userInput"
                            required
                            className="form-control"
                            value={this.state.username}
                            onChange={this.onChangeUsername}>
                                {
                                    this.state.users.map(function(user) { // retourne l'utilisateur
                                        return <option
                                        key={user}
                                        value={user}>{user}
                                        </option>;
                                    })
                                }
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input type="text" 
                        className="form-control" 
                        value={this.state.description} 
                        onChange={this.onChangeDescription} />
                    </div>

                    <div className="form-group">
                        <label>Duration (in minutes): </label>
                        <input type="text" 
                        className="form-control"
                        value={this.state.duration} 
                        onChange={this.onChangeDuration}/>
                    </div>

                    <div className="form-group">
                        <label>Date: </label>
                        <div>
                            <DatePicker
                                selected={this.state.date}
                                onChange={this.onChangeDate}
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <button type="submit" className="btn btn-primary">Create exercise log</button>
                    </div>
                </form>
            </div>
        )
    }
}
