import React, { Component } from 'react'

export default class CreateExercise extends Component {
    constructor(props) {
        super(props);

        this.onChangeUsername = this.onChangeUsername.bind(this); //permet de faire en sorte que this se réferre par rapport à la classe
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onChangeSubmit.bind(this);

        this.state = {
            username: '',
            description: '',
            duration: 0,
            date: new Date(),
            users: []
        }
    }

    componentDidMount() {
        this.setState({
            users: ['test user'],
            username: 'test user'
        })
    }

    onChangeUsername(e) { //quand l'utilisateur remplit le username dans le formulaire, ça va appeler cette fonction
        this.setState({
            username: e.target.value //le target c'est la textbox et la value est la valeur que l'utilisateur entre
        });
    }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }

    onChangeDuration(e) {
        this.setState({
            duration: e.target.value
        });
    }

    onChangeDate(date) {
        this.setState({
            date: date
        });
    }

    onSubmit(e){
        e.preventDefault(); //empêche le formulaire d'être envoyé par défaut (si les cases sont vides)

        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date
        }

        console.log(exercise);

        window.location = '/';
    }

    render() {
        return (
            <div>
                <h3>Create new exercise </h3>
                <form onSubmit={this.onSubmit}>
                    <div class="form-group">
                        <label>Username: </label>
                        <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
                        <select ref="userInput" 
                        required 
                        className="form-control" 
                        value={this.state.username}
                        onChange={this.onChangeUsername}>
                            {
                                this.state.users.map(function(user) { // retourne l'utilisateur
                                    return <option
                                    key={user}
                                    value={user}>{user}>
                                    </option>;
                                })
                            }
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input type="text" className="form-control" 
                        value={this.state.description} 
                        onChange={this.onChangeDescription} 
                        placeholder="Password" />
                    </div>

                    <div class="form-group">
                        <label>Duration (in minutes): </label>
                        <input type="text" 
                        className="form-control"
                        value={this.state.duration} 
                        onChange={this.onChangeDuration}/>
                    </div>

                    <div class="form-group">
                        <label>Date: </label>
                        <div>
                            <DatePicker
                                selected={this.state.date}/>
                        </div>
                    </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
                </form>
            </div>
        )
    }
}
